css/projects.css.content:
/* =====================================================================
   PROJECT PAGE SPECIFIC STYLES
   ===================================================================== */

/* Project Header Section */
.project-header {
    background: linear-gradient(135deg, #0d6efd 0%, #0a58ca 100%);
    position: relative;
    overflow: hidden;
}

.project-header::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 4px;
    background: linear-gradient(90deg, transparent, #ffffff, transparent);
    opacity: 0.3;
}

.project-stats .stat-box {
    min-width: 120px;
    border-radius: 10px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
    transition: all 0.3s ease;
}

.project-stats .stat-box:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

/* Project Filters */
.project-filters {
    margin-bottom: 2rem;
}

.filter-btn {
    border-radius: 50px;
    padding: 8px 20px;
    margin: 0 5px 10px;
    font-weight: 500;
    transition: all 0.3s ease;
}

.filter-btn:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(13, 110, 253, 0.2);
}

.filter-btn.active {
    background-color: #0d6efd;
    color: white;
    border-color: #0d6efd;
}

/* Project Cards */
.project-card {
    height: 100%;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    border: none;
    border-radius: 15px;
    overflow: hidden;
}

.project-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1) !important;
}

.project-card .card-img-top {
    height: 200px;
    object-fit: cover;
    transition: transform 0.5s ease;
}

.project-card:hover .card-img-top {
    transform: scale(1.05);
}

.project-badge {
    position: absolute;
    top: 15px;
    right: 15px;
    z-index: 10;
    font-size: 0.75rem;
    padding: 5px 10px;
    border-radius: 20px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);
}

.tech-stack {
    display: flex;
    flex-wrap: wrap;
    gap: 5px;
    margin-bottom: 15px;
}

.tech-badge {
    background: linear-gradient(135deg, #0d6efd 0%, #0b5ed7 100%);
    color: white;
    padding: 3px 8px;
    border-radius: 12px;
    font-size: 0.7rem;
    font-weight: 500;
}

/* Project Process Section */
.process-card {
    background-color: white;
    border-radius: 15px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    height: 100%;
    transition: all 0.3s ease;
}

.process-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
}

.process-icon {
    width: 80px;
    height: 80px;
    margin: 0 auto;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgba(13, 110, 253, 0.1);
    border-radius: 50%;
    position: relative;
}

.process-icon i {
    font-size: 2rem;
    color: #0d6efd;
}

.process-number {
    position: absolute;
    top: -5px;
    right: -5px;
    width: 30px;
    height: 30px;
    background-color: #0d6efd;
    color: white;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: bold;
    font-size: 1rem;
}

/* Project Modals */
.modal-content {
    border: none;
    border-radius: 15px;
    overflow: hidden;
}

.modal-header {
    border-bottom: none;
}

.modal-footer {
    border-top: none;
}

/* Animation for filter transitions */
.project-item {
    transition: all 0.5s ease;
}

.project-item.hide {
    opacity: 0;
    transform: scale(0.8);
    height: 0;
    margin: 0;
    padding: 0;
    overflow: hidden;
}

/* No Results Message */
#noResults {
    opacity: 0;
    transition: opacity 0.5s ease;
}

#noResults.show {
    opacity: 1;
}

/* Responsive Adjustments */
@media (max-width: 991.98px) {
    .project-header {
        text-align: center;
    }
    
    .project-stats {
        justify-content: center !important;
        margin-top: 2rem !important;
    }
}

@media (max-width: 767.98px) {
    .process-card {
        margin-bottom: 1rem;
    }
    
    .filter-btn {
        font-size: 0.85rem;
        padding: 6px 15px;
    }
}
/* Search Bar Styling */
.search-wrapper {
    margin-bottom: 1.5rem;
}

.search-wrapper .input-group {
    box-shadow: 0 3px 10px rgba(0, 0, 0, 0.05);
    border-radius: 50px;
    overflow: hidden;
}

.search-wrapper .input-group-text {
    border-radius: 50px 0 0 50px;
    border-right: none;
}

.search-wrapper .form-control {
    border-radius: 0 50px 50px 0;
    font-size: 0.95rem;
    height: 50px;
    border-left: none;
}

.search-wrapper .form-control:focus {
    box-shadow: none;
    border-color: #ced4da;
}

.search-wrapper .btn {
    border-radius: 50px;
    width: 50px;
    height: 50px;
    padding: 0;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-left: 10px;
}

.search-wrapper .btn i {
    font-size: 0.9rem;
}

#searchFeedback {
    min-height: 24px;
    font-size: 0.9rem;
}

/* Highlight search matches */
.search-highlight {
    background-color: rgba(255, 193, 7, 0.3);
    padding: 0 2px;
    border-radius: 3px;
}

/* Search animation */
@keyframes pulse {
    0% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
    }
}

.search-pulse {
    animation: pulse 0.5s ease-in-out;
}

js/interface.js.content:
// =====================================================================
// PROFESSIONAL LOADING SCREEN - Simplified and Reliable
// =====================================================================
class ProfessionalLoader {
    constructor() {
        this.loadingScreen = document.getElementById('loadingScreen');
        this.progressFill = document.querySelector('.progress-fill');
        this.progressPercentage = document.querySelector('.progress-percentage');
        this.loadingText = document.querySelector('.loading-text');

        this.progress = 0;
        this.startTime = Date.now();
        this.duration = 1000; // Complete in 1 second

        this.messages = [
            'Initializing...',
            'Loading assets...',
            'Preparing interface...',
            'Almost ready...',
            'Welcome!'
        ];

        // Check if elements exist
        if (!this.loadingScreen) {
            return;
        }

        this.init();
    }

    init() {
        // Prevent scrolling during loading
        document.body.style.overflow = 'hidden';

        // Start smooth animation
        this.animate();
    }

    animate() {
        const elapsed = Date.now() - this.startTime;

        // Calculate progress as percentage of duration (0-100)
        this.progress = Math.min((elapsed / this.duration) * 100, 100);

        // Update UI
        this.updateProgress();

        // Continue animation until complete
        if (this.progress < 100) {
            requestAnimationFrame(() => this.animate());
        } else {
            // Finished - hide loading screen immediately
            setTimeout(() => this.hideLoading(), 200);
        }
    }

    updateProgress() {
        const roundedProgress = Math.round(this.progress);

        // Update progress bar
        if (this.progressFill) {
            this.progressFill.style.width = roundedProgress + '%';
        }

        // Update percentage text
        if (this.progressPercentage) {
            this.progressPercentage.textContent = roundedProgress + '%';
        }

        // Update loading message based on progress
        if (this.loadingText) {
            let messageIndex;
            if (roundedProgress < 25) {
                messageIndex = 0; // Initializing...
            } else if (roundedProgress < 50) {
                messageIndex = 1; // Loading assets...
            } else if (roundedProgress < 75) {
                messageIndex = 2; // Preparing interface...
            } else if (roundedProgress < 95) {
                messageIndex = 3; // Almost ready...
            } else {
                messageIndex = 4; // Welcome!
            }
            this.loadingText.textContent = this.messages[messageIndex];
        }
    }

    hideLoading() {
        if (this.loadingScreen) {
            this.loadingScreen.classList.add('fade-out');

            // Re-enable scrolling
            document.body.style.overflow = 'auto';

            // Remove loading screen from DOM after fade-out
            setTimeout(() => {
                if (this.loadingScreen && this.loadingScreen.parentNode) {
                    this.loadingScreen.parentNode.removeChild(this.loadingScreen);
                }
            }, 600);
        }
    }
}

// Initialize loading screen when DOM is ready
if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', () => {
        new ProfessionalLoader();
    });
} else {
    // DOM already loaded
    new ProfessionalLoader();
}

// Preload logo image
const preloadLogo = () => {
    const logoImg = new Image();
    logoImg.src = 'images/logos/peter-logo.png';
};

if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', preloadLogo);
} else {
    preloadLogo();
}

css/contact.css.content:
/* Contact Page Specific Styles */

/* Header */
.contact-header {
    background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1516387938699-a93567ec168e?w=1200&auto=format&fit=crop') no-repeat center center;
    background-size: cover;
    padding: 6rem 0;
}

/* Contact Cards */
.contact-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    text-align: center;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.contact-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
}

.contact-icon {
    font-size: 2.5rem;
    color: var(--bs-primary);
    margin-bottom: 1.5rem;
}

.contact-link {
    display: inline-block;
    color: var(--bs-primary);
    font-weight: 600;
    text-decoration: none;
    transition: color 0.3s ease;
}

.contact-link:hover {
    color: var(--bs-primary-dark);
    text-decoration: underline;
}

/* Availability Badge */
.availability-info {
    margin-top: 3rem;
}

.availability-badge {
    display: inline-block;
    background-color: #f8f9fa;
    border-radius: 30px;
    padding: 0.5rem 1.5rem;
    font-weight: 600;
    box-shadow: 0 3px 10px rgba(0, 0, 0, 0.05);
}

.status-dot {
    display: inline-block;
    width: 10px;
    height: 10px;
    background-color: #28a745;
    border-radius: 50%;
    margin-right: 8px;
    animation: pulse 2s infinite;
}

@keyframes pulse {
    0% {
        box-shadow: 0 0 0 0 rgba(40, 167, 69, 0.7);
    }
    70% {
        box-shadow: 0 0 0 10px rgba(40, 167, 69, 0);
    }
    100% {
        box-shadow: 0 0 0 0 rgba(40, 167, 69, 0);
    }
}

/* Contact Form */
.contact-form-wrapper {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
}

.contact-form-intro {
    padding-right: 2rem;
}

.feature-icon {
    font-size: 1.5rem;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    background-color: #f8f9fa;
}

.form-control, .form-select {
    padding: 0.75rem 1rem;
    border-radius: 8px;
    border: 1px solid #dee2e6;
    transition: border-color 0.3s ease, box-shadow 0.3s ease;
}

.form-control:focus, .form-select:focus {
    border-color: var(--bs-primary);
    box-shadow: 0 0 0 0.25rem rgba(13, 110, 253, 0.25);
}

.form-label {
    font-weight: 600;
    margin-bottom: 0.5rem;
}

/* Social Grid */
.social-grid {
    display: grid;
    grid-template-columns: repeat(6, 1fr);
    gap: 1rem;
    max-width: 800px;
    margin: 0 auto;
}

.social-grid-item {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 1.5rem;
    border-radius: 10px;
    color: #fff;
    text-decoration: none;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.social-grid-item:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
    color: #fff;
}

.social-grid-item i {
    font-size: 2rem;
    margin-bottom: 0.5rem;
}

/* Social colors */
.social-grid-item.twitter {
    background-color: #1DA1F2;
}

.social-grid-item.instagram {
    background: linear-gradient(45deg, #405DE6, #5851DB, #833AB4, #C13584, #E1306C, #FD1D1D);
}

.social-grid-item.linkedin {
    background-color: #0077B5;
}

.social-grid-item.tiktok {
    background-color: #000000;
}

.social-grid-item.youtube {
    background-color: #FF0000;
}

.social-grid-item.whatsapp {
    background-color: #25D366;
}

/* Location Map */
.location-map {
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

/* FAQ Section */
.accordion-item {
    border: none;
    border-bottom: 1px solid #dee2e6;
    border-radius: 0 !important;
}

.accordion-button {
    font-weight: 600;
    color: #212529;
}

.accordion-button:not(.collapsed) {
    background-color: #f8f9fa;
    color: var(--bs-primary);
    box-shadow: none;
}

.accordion-button:focus {
    box-shadow: none;
    border-color: rgba(0,0,0,.125);
}

.accordion-button::after {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 16 16' fill='%230d6efd'%3e%3cpath fill-rule='evenodd' d='M1.646 4.646a.5.5 0 0 1 .708 0L8 10.293l5.646-5.647a.5.5 0 0 1 .708.708l-6 6a.5.5 0 0 1-.708 0l-6-6a.5.5 0 0 1 0-.708z'/%3e%3c/svg%3e");
}

.accordion-body {
    padding: 1.5rem;
    background-color: #f8f9fa;
}

/* Section Title */
.section-title {
    position: relative;
    padding-bottom: 15px;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 80px;
    height: 3px;
    background-color: var(--bs-primary);
}

.text-center .section-title::after {
    left: 50%;
    transform: translateX(-50%);
}

/* Form Message */
#formMessage {
    border-radius: 8px;
    padding: 1rem;
}

/* Responsive Adjustments */
@media (max-width: 991.98px) {
    .contact-form-intro {
        padding-right: 0;
        margin-bottom: 2rem;
    }
}

@media (max-width: 767.98px) {
    .contact-header {
        padding: 4rem 0;
    }
    
    .social-grid {
        grid-template-columns: repeat(3, 1fr);
    }
}

@media (max-width: 575.98px) {
    .social-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}
/* Enhanced Contact Form Styles */
.form-section {
    background: rgba(255, 255, 255, 0.8);
    border-radius: 12px;
    padding: 1.5rem;
    border-left: 4px solid var(--primary-color);
    margin-bottom: 1.5rem;
}

.form-section-title {
    color: var(--primary-color);
    font-size: 1.1rem;
    font-weight: 600;
    margin-bottom: 1rem;
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.service-item {
    display: flex;
    align-items: center;
    padding: 0.5rem;
    background: rgba(255, 255, 255, 0.7);
    border-radius: 8px;
    margin-bottom: 0.5rem;
}

.contact-form-wrapper {
    background: linear-gradient(135deg, rgba(102, 126, 234, 0.1), rgba(118, 75, 162, 0.1));
    border-radius: 20px;
    padding: 2rem;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.form-control, .form-select {
    border-radius: 10px;
    border: 2px solid #e9ecef;
    padding: 0.75rem 1rem;
    transition: all 0.3s ease;
}

.form-control:focus, .form-select:focus {
    border-color: var(--primary-color);
    box-shadow: 0 0 0 0.2rem rgba(102, 126, 234, 0.25);
}

.form-check-input:checked {
    background-color: var(--primary-color);
    border-color: var(--primary-color);
}

.pricing-note {
    border: 2px dashed var(--primary-color);
    animation: pulse 2s infinite;
}

@keyframes pulse {
    0%, 100% { opacity: 1; }
    50% { opacity: 0.8; }
}

/* File upload styling */
.form-control[type="file"] {
    padding: 0.5rem;
}

.form-control[type="file"]::-webkit-file-upload-button {
    background: var(--primary-color);
    color: white;
    border: none;
    padding: 0.5rem 1rem;
    border-radius: 6px;
    margin-right: 1rem;
    cursor: pointer;
}

/* Responsive adjustments */
@media (max-width: 768px) {
    .form-section {
        padding: 1rem;
    }
    
    .contact-form-wrapper {
        padding: 1.5rem;
    }
    
    .service-item {
        font-size: 0.9rem;
    }
}

css/cv.css.content:
/* CV Page Specific Styles */

/* Header */
.cv-header {
    background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1586281380349-632531db7ed4?w=1200&auto=format&fit=crop') no-repeat center center;
    background-size: cover;
    padding: 6rem 0;
}

/* Section Title */
.section-title {
    position: relative;
    padding-bottom: 15px;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 80px;
    height: 3px;
    background-color: var(--bs-primary);
}

.text-center .section-title::after {
    left: 50%;
    transform: translateX(-50%);
}

/* CV Features */
.cv-features {
    margin-bottom: 2rem;
}

.cv-feature {
    display: flex;
    align-items: center;
    margin-bottom: 0.75rem;
}

.cv-feature i {
    margin-right: 0.75rem;
    font-size: 1.2rem;
}

/* CV Preview */
.cv-preview-wrapper {
    position: relative;
    padding: 1rem;
    background-color: #fff;
    border-radius: 10px;
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.cv-preview {
    position: relative;
    overflow: hidden;
    border-radius: 5px;
}

.cv-preview img {
    width: 100%;
    height: auto;
    transition: transform 0.3s ease;
}

.cv-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.cv-preview:hover .cv-overlay {
    opacity: 1;
}

.cv-preview:hover img {
    transform: scale(1.05);
}

/* Highlight Cards */
.highlight-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    height: 100%;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    text-align: center;
}

.highlight-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
}

.highlight-icon {
    font-size: 3rem;
    color: var(--bs-primary);
    margin-bottom: 1.5rem;
}

.highlight-card h3 {
    font-size: 1.3rem;
    font-weight: 600;
    margin-bottom: 1.5rem;
}

.highlight-list {
    list-style: none;
    padding-left: 0;
    text-align: left;
}

.highlight-list li {
    position: relative;
    padding-left: 1.5rem;
    margin-bottom: 0.75rem;
}

.highlight-list li::before {
    content: '\F633';
    font-family: 'bootstrap-icons';
    position: absolute;
    left: 0;
    color: var(--bs-primary);
}

/* Document Cards */
.document-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    height: 100%;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    text-align: center;
}

.document-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
}

.document-icon {
    font-size: 3rem;
    color: var(--bs-primary);
    margin-bottom: 1.5rem;
}

.document-card h3 {
    font-size: 1.3rem;
    font-weight: 600;
    margin-bottom: 1rem;
}

.document-card p {
    color: #6c757d;
    margin-bottom: 1.5rem;
}

/* Contact Form */
.contact-form-wrapper {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
}

.form-control {
    padding: 0.75rem 1rem;
    border-radius: 8px;
}

.form-control:focus {
    border-color: var(--bs-primary);
    box-shadow: 0 0 0 0.25rem rgba(13, 110, 253, 0.25);
}

/* Download Tracking Modal */
.modal-content {
    border-radius: 15px;
    overflow: hidden;
}

.modal-header {
    background-color: var(--bs-primary);
    color: white;
}

/* Responsive Adjustments */
@media (max-width: 991.98px) {
    .cv-header {
        padding: 4rem 0;
    }
    
    .highlight-card, .document-card {
        margin-bottom: 1.5rem;
    }
}

@media (max-width: 767.98px) {
    .cv-preview-wrapper {
        margin-top: 2rem;
    }
    
    .highlight-card, .document-card {
        padding: 1.5rem;
    }
    
    .highlight-icon, .document-icon {
        font-size: 2.5rem;
        margin-bottom: 1rem;
    }
}

css/about.css.content:
/* About Page Specific Styles */

/* Header */
.about-header {
    background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1522071820081-009f0129c71c?w=1200&auto=format&fit=crop') no-repeat center center;
    background-size: cover;
    padding: 6rem 0;
}

/* Profile Image */
.profile-image-container {
    position: relative;
    display: inline-block;
}

.profile-image {
    width: 280px;
    height: 280px;
    object-fit: cover;
    border: 5px solid rgba(255, 255, 255, 0.3);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
    transition: transform 0.3s ease;
}

.profile-image:hover {
    transform: scale(1.05);
}

.profile-badge {
    position: absolute;
    bottom: 10px;
    right: 10px;
    background-color: var(--bs-primary);
    color: white;
    padding: 5px 15px;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
}

/* Story Section */
.story-content {
    font-size: 1.1rem;
    line-height: 1.8;
}

.section-title {
    position: relative;
    padding-bottom: 15px;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 3px;
    background-color: var(--bs-primary);
}

/* Values */
.value-item {
    padding: 1.5rem;
    background-color: #fff;
    border-radius: 10px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    transition: transform 0.3s ease;
    height: 100%;
}

.value-item:hover {
    transform: translateY(-10px);
}

.value-icon {
    font-size: 2rem;
}

/* Skills Section */
.skills-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
}

.skill-item {
    margin-bottom: 1.5rem;
}

.skill-name {
    font-weight: 600;
}

.skill-percentage {
    font-weight: 600;
    color: var(--bs-primary);
}

.progress {
    height: 8px;
    border-radius: 4px;
    background-color: #e9ecef;
}

.progress-bar {
    border-radius: 4px;
}

/* Soft Skills */
.soft-skill-item {
    text-align: center;
    padding: 1rem;
    background-color: #f8f9fa;
    border-radius: 10px;
    transition: transform 0.3s ease;
    height: 100%;
}

.soft-skill-item:hover {
    transform: translateY(-5px);
    background-color: #e9ecef;
}

.soft-skill-icon {
    font-size: 1.8rem;
    color: var(--bs-primary);
    margin-bottom: 0.5rem;
}

.soft-skill-rating {
    color: #ffc107;
    font-size: 0.9rem;
    margin-top: 0.5rem;
}

/* Tools Section */
.tools-section {
    padding-top: 2rem;
}

.tools-container {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 1rem;
    margin-top: 2rem;
}

.tool-item {
    width: 60px;
    height: 60px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: #fff;
    border-radius: 10px;
    box-shadow: 0 3px 10px rgba(0, 0, 0, 0.1);
    font-size: 1.8rem;
    color: var(--bs-primary);
    transition: all 0.3s ease;
}

.tool-item:hover {
    transform: translateY(-5px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
    color: #fff;
    background-color: var(--bs-primary);
}

.tool-item span {
    font-weight: bold;
    font-size: 1.2rem;
}

/* Timeline */
.timeline {
    position: relative;
    padding: 20px 0;
}

.timeline::before {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 50%;
    width: 3px;
    background-color: #e9ecef;
    transform: translateX(-50%);
}

.timeline-item {
    position: relative;
    margin-bottom: 50px;
}

.timeline-badge {
    position: absolute;
    top: 0;
    left: 50%;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    text-align: center;
    line-height: 40px;
    color: white;
    font-size: 1.2rem;
    transform: translateX(-50%);
    z-index: 1;
}

.timeline-panel {
    width: calc(50% - 40px);
    float: left;
    background-color: #fff;
    border-radius: 10px;
    padding: 20px;
    position: relative;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
}

.timeline-panel::after {
    content: '';
    position: absolute;
    top: 15px;
    right: -15px;
    border-top: 15px solid transparent;
    border-left: 15px solid #fff;
    border-bottom: 15px solid transparent;
}

.timeline-item:nth-child(even) .timeline-panel {
    float: right;
}

.timeline-item:nth-child(even) .timeline-panel::after {
    left: -15px;
    border-left: 0;
    border-right: 15px solid #fff;
}

.timeline-heading {
    margin-bottom: 15px;
}

.timeline-list {
    padding-left: 20px;
    margin-bottom: 0;
}

.timeline-list li {
    margin-bottom: 5px;
}

/* Testimonials */
.testimonial-item {
    padding: 2rem;
}

.testimonial-avatar img {
    width: 80px;
    height: 80px;
    object-fit: cover;
    margin-bottom: 1.5rem;
    border: 3px solid var(--bs-primary);
}

.testimonial-rating {
    color: #ffc107;
}

.testimonial-text {
    font-size: 1.1rem;
    font-style: italic;
    margin-bottom: 1.5rem;
}

.testimonial-indicators {
    position: relative;
    margin-top: 2rem;
    margin-bottom: 0;
    justify-content: center;
}

.testimonial-indicators [data-bs-target] {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background-color: #dee2e6;
}

.testimonial-indicators .active {
    background-color: var(--bs-primary);
}

.carousel-control-prev,
.carousel-control-next {
    width: 40px;
    height: 40px;
    background-color: var(--bs-primary);
    border-radius: 50%;
    top: 50%;
    transform: translateY(-50%);
    opacity: 0.7;
}

.carousel-control-prev {
    left: -50px;
}

.carousel-control-next {
    right: -50px;
}

.carousel-control-prev:hover,
.carousel-control-next:hover {
    opacity: 1;
}

/* Responsive Adjustments */
@media (max-width: 991.98px) {
    .timeline::before {
        left: 40px;
    }
    
    .timeline-badge {
        left: 40px;
    }
    
    .timeline-panel {
        width: calc(100% - 90px);
        float: right;
    }
    
    .timeline-panel::after {
        left: -15px;
        border-left: 0;
        border-right: 15px solid #fff;
    }
    
    .carousel-control-prev {
        left: 10px;
    }
    
    .carousel-control-next {
        right: 10px;
    }
}

@media (max-width: 767.98px) {
    .about-header {
        padding: 4rem 0;
    }
    
    .profile-image {
        width: 200px;
        height: 200px;
    }
    
    .tools-container {
        gap: 0.5rem;
    }
    
    .tool-item {
        width: 50px;
        height: 50px;
        font-size: 1.5rem;
    }
}
/* Accordion Customization */
.accordion-button { 
    background-color: #e9ecef; 
    color: #333; 
    font-weight: 600; 
} 
.accordion-button:not(.collapsed) { 
    background-color: var(--bs-primary); 
    color: #fff; 
}
.accordion-body { 
    background-color: #f8f9fa; 
    color: #555; 

    font-weight: 600;
    line-height: 1.6;
}

/* Timeline Animation */
.timeline-panel {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.timeline-badge {
    transition: transform 0.3s ease;
}

.timeline-item:hover .timeline-badge {
    transform: translateX(-50%) scale(1.2);
}

@media (max-width: 991.98px) {
    .timeline-item:hover .timeline-badge {
        transform: scale(1.2);
    }
}

