    background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1521791136064-7986c2920216?w=1200&auto=format&fit=crop') no-repeat center center;
    background-size: cover;
    padding: 6rem 0;
}

/* Section Title */
.section-title {
    position: relative;
    padding-bottom: 15px;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 3px;
    background-color: var(--bs-primary);
}

/* Testimonial Cards */
.testimonial-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    height: 100%;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    position: relative;
}

.testimonial-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
}

.testimonial-rating {
    color: #ffc107;
    font-size: 1.2rem;
    margin-bottom: 1rem;
}

.testimonial-content {
    font-style: italic;
    margin-bottom: 1.5rem;
    position: relative;
}

.testimonial-content::before {
    content: '\201C';
    font-size: 4rem;
    font-family: Georgia, serif;
    color: rgba(0, 0, 0, 0.1);
    position: absolute;
    top: -20px;
    left: -10px;
    z-index: -1;
}

.testimonial-author {
    display: flex;
    align-items: center;
}

.testimonial-avatar {
    width: 50px;
    height: 50px;
    border-radius: 50%;
    object-fit: cover;
    margin-right: 1rem;
    border: 2px solid var(--bs-primary);
}

.testimonial-author h4 {
    font-size: 1rem;
    font-weight: 600;
    margin-bottom: 0.2rem;
}

.testimonial-author p {
    font-size: 0.85rem;
    color: #6c757d;
    margin-bottom: 0;
}

.testimonial-service {
    position: absolute;
    top: 1rem;
    right: 1rem;
}

/* Video Testimonials */
.video-testimonial {
    background-color: #fff;
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.video-testimonial:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
}

.video-testimonial-info {
    padding: 1.5rem;
    text-align: center;
}

.video-testimonial-info h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 0.2rem;
}

.video-testimonial-info p {
    font-size: 0.9rem;
    color: #6c757d;
    margin-bottom: 0;
}

/* Testimonial Stats */
.testimonial-stats {
    padding: 3rem 0;
}

.stat-item {
    text-align: center;
}

.stat-icon {
    font-size: 2.5rem;
    color: var(--bs-primary);
    margin-bottom: 1rem;
}

.stat-number {
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 0.5rem;
    color: #212529;
}

.stat-label {
    font-size: 1rem;
    color: #6c757d;
    margin-bottom: 0;
}

/* Testimonial Form */
.testimonial-form-wrapper {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
}

.form-control, .form-select {
    padding: 0.75rem 1rem;
    border-radius: 8px;
    border: 1px solid #dee2e6;
    transition: border-color 0.3s ease, box-shadow 0.3s ease;
}

.form-control:focus, .form-select:focus {
    border-color: var(--bs-primary);
    box-shadow: 0 0 0 0.25rem rgba(13, 110, 253, 0.25);
}

.form-label {
    font-weight: 600;
    margin-bottom: 0.5rem;
}

/* Rating Input */
.rating-input {
    display: flex;
    flex-direction: row-reverse;
    justify-content: flex-end;
}

.rating-input input {
    display: none;
}

.rating-input label {
    cursor: pointer;
    font-size: 1.5rem;
    color: #ccc;
    margin-right: 0.25rem;
}

.rating-input label:hover,
.rating-input label:hover ~ label,
.rating-input input:checked ~ label {
    color: #ffc107;
}

/* Featured Testimonial */
.featured-testimonial {
    background-color: #fff;
    border-radius: 15px;
    padding: 2.5rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    position: relative;
}

.featured-testimonial::before {
    content: '\201C';
    font-size: 6rem;
    font-family: Georgia, serif;
    color: rgba(0, 0, 0, 0.1);
    position: absolute;
    top: 10px;
    left: 20px;
    z-index: 0;
}

.featured-testimonial .testimonial-text {
    font-size: 1.1rem;
    line-height: 1.8;
    margin-bottom: 1.5rem;
    position: relative;
    z-index: 1;
}

.featured-testimonial .testimonial-author {
    display: flex;
    align-items: center;
}

.featured-testimonial .testimonial-avatar {
    width: 60px;
    height: 60px;
}

/* Form Message */
#testimonialFormMessage {
    border-radius: 8px;
    padding: 1rem;
}

/* Responsive Adjustments */
@media (max-width: 991.98px) {
    .testimonials-header {
        padding: 4rem 0;
    }
}

@media (max-width: 767.98px) {
    .stat-item {
        margin-bottom: 2rem;
    }
    
    .featured-testimonial {
        padding: 1.5rem;
    }
    
    .featured-testimonial::before {
        font-size: 4rem;
    }
}

