/**
 * Testimonials page specific JavaScript
 */
document.addEventListener('DOMContentLoaded', function() {
    // --- CONSTANTS ---
    const testimonialForm = document.getElementById('leaveTestimonialForm');
    const submitTestimonialBtn = document.getElementById('submitTestimonialBtn');
    const testimonialFormMessage = document.getElementById('testimonialFormMessage');
    const newsletterForm = document.getElementById('newsletterForm');
    const newsletterBtn = document.getElementById('newsletterBtn');
    const newsletterMessage = document.getElementById('newsletterMessage');
    const backToTopBtn = document.getElementById('backToTop');
    const ratingInputs = document.querySelectorAll('.rating-input input');

    // --- INITIALIZATIONS ---
    // Initialize tooltips
    const tooltipTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]'));
    tooltipTriggerList.map(function (tooltipTriggerEl) {
        return new bootstrap.Tooltip(tooltipTriggerEl);
    });

    // --- TESTIMONIAL FORM SUBMISSION ---
    if (testimonialForm) {
        testimonialForm.addEventListener('submit', async function(e) {
            e.preventDefault();
            
            // Update button state
            const originalText = submitTestimonialBtn.innerHTML;
            submitTestimonialBtn.innerHTML = '<i class="bi bi-hourglass-split me-2"></i>Submitting...';
            submitTestimonialBtn.disabled = true;
            
            try {
                const response = await fetch(testimonialForm.action, {
                    method: 'POST',
                    body: new FormData(testimonialForm),
                    headers: { 'Accept': 'application/json' }
                });
                
                if (response.ok) {
                    // Success message
                    testimonialFormMessage.className = 'alert alert-success';
                    testimonialFormMessage.innerHTML = `
                        <div class="d-flex align-items-center">
                            <i class="bi bi-check-circle-fill text-success fs-4 me-3"></i>
                            <div>
                                <h5 class="mb-1">Thank You for Your Testimonial!</h5>
                                <p class="mb-0">Your feedback has been submitted successfully and will be reviewed shortly.</p>
                            </div>
                        </div>
                    `;
                    testimonialForm.reset();
                    
                    // Reset rating stars
                    ratingInputs.forEach(input => {
                        input.checked = false;
                    });
                    
                    // Track form submission
                    if (typeof gtag !== 'undefined' && localStorage.getItem('cookiesAccepted') === 'true') {
                        gtag('event', 'testimonial_submission', {
                            'event_category': 'engagement',
                            'event_label': document.getElementById('service').value
                        });
                    }
                } else {
                    throw new Error('Network response was not ok');
                }
            } catch (error) {
                // Error message
                testimonialFormMessage.className = 'alert alert-danger';
                testimonialFormMessage.innerHTML = `
                    <div class="d-flex align-items-center">
                        <i class="bi bi-exclamation-triangle-fill text-danger fs-4 me-3"></i>
                        <div>
                            <h5 class="mb-1">Oops! Something went wrong.</h5>
                            <p class="mb-0">Please try again or contact me directly at petereluwade55@gmail.com</p>
                        </div>
                    </div>
                `;
            } finally {
                // Reset button state and show message
                submitTestimonialBtn.innerHTML = originalText;
                submitTestimonialBtn.disabled = false;
                testimonialFormMessage.style.display = 'block';
                
                // Scroll to the message
                testimonialFormMessage.scrollIntoView({ behavior: 'smooth', block: 'center' });
                
                // Hide message after 10 seconds if it's a success message
                if (testimonialFormMessage.classList.contains('alert-success')) {
                    setTimeout(() => {
                        testimonialFormMessage.style.display = 'none';
                    }, 10000);
                }
            }
        });
    }

    // --- RATING STARS INTERACTION ---
    // Add visual feedback when hovering over rating stars
    if (ratingInputs.length > 0) {
        const ratingLabels = document.querySelectorAll('.rating-input label');
        
        ratingLabels.forEach(label => {
            label.addEventListener('mouseover', function() {
                // Reset all stars
                ratingLabels.forEach(l => l.classList.remove('hovered'));
                
                // Add hovered class to current and previous stars
                let currentLabel = this;
                while (currentLabel) {
                    currentLabel.classList.add('hovered');
                    currentLabel = currentLabel.previousElementSibling;
                    if (currentLabel && currentLabel.tagName !== 'LABEL') {
                        currentLabel = currentLabel.previousElementSibling;
                    }
                }
            });
        });
        
        document.querySelector('.rating-input').addEventListener('mouseleave', function() {
            ratingLabels.forEach(label => label.classList.remove('hovered'));
        });
    }

    // --- TESTIMONIAL CARD ANIMATIONS ---
    // Add hover effect to testimonial cards
    const testimonialCards = document.querySelectorAll('.testimonial-card');
    testimonialCards.forEach(card => {
        card.addEventListener('mouseenter', function() {
            this.style.transform = 'translateY(-10px)';
            this.style.boxShadow = '0 15px 30px rgba(0, 0, 0, 0.1)';
        });
        
        card.addEventListener('mouseleave', function() {
            this.style.transform = '';
            this.style.boxShadow = '';
        });
    });

    // --- BACK TO TOP BUTTON ---
    if (backToTopBtn) {
        window.addEventListener('scroll', function() {
            if (window.pageYOffset > 300) {
                backToTopBtn.classList.add('show');
            } else {
                backToTopBtn.classList.remove('show');
            }
        });
        backToTopBtn.addEventListener('click', function() {
            window.scrollTo({ top: 0, behavior: 'smooth' });
        });
    }

    // --- NEWSLETTER FORM ---
    if (newsletterForm) {
        newsletterForm.addEventListener('submit', async function(e) {
            e.preventDefault();
            const originalText = newsletterBtn.innerHTML;
            newsletterBtn.innerHTML = '<i class="bi bi-hourglass-split"></i> Subscribing...';
            newsletterBtn.disabled = true;
            
            try {
                const response = await fetch(newsletterForm.action, {
                    method: 'POST',
                    body: new FormData(newsletterForm),
                    headers: { 'Accept': 'application/json' }
                });
                if (response.ok) {
                    newsletterMessage.className = 'alert alert-success mt-2';
                    newsletterMessage.innerHTML = `<i class="bi bi-check-circle me-2"></i><strong>Success!</strong> Thank you for subscribing!`;
                    newsletterForm.reset();
                } else {
                    throw new Error('Network response was not ok');
                }
            } catch (error) {
                newsletterMessage.className = 'alert alert-danger mt-2';
                newsletterMessage.innerHTML = `<i class="bi bi-exclamation-triangle me-2"></i><strong>Oops!</strong> Something went wrong. Please try again.`;
            } finally {
                newsletterMessage.style.display = 'block';
                newsletterBtn.innerHTML = originalText;
                newsletterBtn.disabled = false;
                setTimeout(() => { newsletterMessage.style.display = 'none'; }, 5000);
            }
        });
    }

    // --- STATS COUNTER ANIMATION ---
    // Animate the stats numbers when they come into view
    const statNumbers = document.querySelectorAll('.stat-number');
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const statNumber = entry.target;
                const finalValue = parseInt(statNumber.textContent);
                let startValue = 0;
                
                // Handle special case for decimal values (like ratings)
                if (statNumber.textContent.includes('.')) {
                    animateDecimalValue(statNumber, 0, parseFloat(statNumber.textContent), 1500);
                } else {
                    const duration = 2000;
                    const increment = Math.ceil(finalValue / (duration / 16));
                    
                    const timer = setInterval(() => {
                        startValue += increment;
                        if (startValue > finalValue) {
                            statNumber.textContent = finalValue;
                            clearInterval(timer);
                        } else {
                            statNumber.textContent = startValue;
                        }
                    }, 16);
                }
                
                observer.unobserve(statNumber);
            }
        });
    }, { threshold: 0.5 });

    statNumbers.forEach(number => {
        observer.observe(number);
    });

    // Helper function to animate decimal values
    function animateDecimalValue(element, start, end, duration) {
        let startTimestamp = null;
        const step = (timestamp) => {
            if (!startTimestamp) startTimestamp = timestamp;
            const progress = Math.min((timestamp - startTimestamp) / duration, 1);
            const currentValue = start + progress * (end - start);
            element.textContent = currentValue.toFixed(1);
            if (progress < 1) {
                window.requestAnimationFrame(step);
            }
        };
        window.requestAnimationFrame(step);
    }

    // --- FORM FIELD VALIDATION ---
    // Add custom validation styles
    const formInputs = document.querySelectorAll('.form-control, .form-select');
    formInputs.forEach(input => {
        input.addEventListener('blur', function() {
            if (this.checkValidity()) {
                this.classList.add('is-valid');
                this.classList.remove('is-invalid');
            } else if (this.value !== '') {
                this.classList.add('is-invalid');
                this.classList.remove('is-valid');
            }
        });
    });

    // --- SMOOTH SCROLL FOR ANCHOR LINKS ---
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function(e) {
            const targetId = this.getAttribute('href');
            if (targetId !== '#' && document.querySelector(targetId)) {
                e.preventDefault();
                document.querySelector(targetId).scrollIntoView({
                    behavior: 'smooth',
                    block: 'start'
                });
            }
        });
    });
});

// --- GLOBAL FUNCTIONS ---
// Cookie consent functions
function acceptCookies() {
    localStorage.setItem('cookiesAccepted', 'true');
    document.getElementById('cookieConsent').classList.remove('show');
    if (typeof gtag !== 'undefined') {
        gtag('config', 'G-9NKNYWDH33');
    }
}

function declineCookies() {
    localStorage.setItem('cookiesAccepted', 'false');
    document.getElementById('cookieConsent').classList.remove('show');
    window['ga-disable-G-9NKNYWDH33'] = true;
}

css/certifications.css.content:
/* Certifications Page Specific Styles */

/* Header */
.certifications-header {
    background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1517245386807-bb43f82c33c4?w=1200&auto=format&fit=crop') no-repeat center center;
    background-size: cover;
    padding: 6rem 0;
}

/* Section Title */
.section-title {
    position: relative;
    padding-bottom: 15px;
}

.section-title::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    width: 80px;
    height: 3px;
    background-color: var(--bs-primary);
}

/* Certification Cards */
.certification-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    height: 100%;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    display: flex;
    flex-direction: column;
}

.certification-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
}

.certification-logo {
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    height: 80px;
}

.certification-logo img {
    max-height: 100%;
    max-width: 100%;
    object-fit: contain;
}

.certification-content {
    flex: 1;
}

.certification-content h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}

.certification-issuer {
    color: #6c757d;
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
}

.certification-date {
    color: #6c757d;
    font-size: 0.85rem;
    margin-bottom: 1rem;
}

.certification-skills {
    margin-bottom: 1rem;
}

.certification-skills .badge {
    margin-right: 0.5rem;
    margin-bottom: 0.5rem;
    padding: 0.5rem 0.75rem;
    font-weight: 500;
}

/* Certificate Image in Modal */
.certificate-img {
    max-width: 100%;
    border-radius: 8px;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

/* Skills Section */
.skill-item {
    margin-bottom: 1.5rem;
}

.skill-name {
    font-weight: 600;
}

.skill-percentage {
    font-weight: 600;
    color: var(--bs-primary);
}

.progress {
    height: 8px;
    border-radius: 4px;
    background-color: #e9ecef;
}

.progress-bar {
    border-radius: 4px;
    transition: width 1s ease-in-out;
}

/* Timeline */
.timeline {
    position: relative;
    padding: 20px 0;
}

.timeline::before {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 50%;
    width: 3px;
    background-color: #e9ecef;
    transform: translateX(-50%);
}

.timeline-item {
    position: relative;
    margin-bottom: 50px;
}

.timeline-item:last-child {
    margin-bottom: 0;
}

.timeline-badge {
    position: absolute;
    top: 0;
    left: 50%;
    width: 40px;
    height: 40px;
    border-radius: 50%;
    text-align: center;
    line-height: 40px;
    color: white;
    font-size: 1.2rem;
    transform: translateX(-50%);
    z-index: 1;
}

.timeline-panel {
    width: calc(50% - 40px);
    float: left;
    background-color: #fff;
    border-radius: 10px;
    padding: 20px;
    position: relative;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.timeline-panel:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
}

.timeline-panel::after {
    content: '';
    position: absolute;
    top: 15px;
    right: -15px;
    border-top: 15px solid transparent;
    border-left: 15px solid #fff;
    border-bottom: 15px solid transparent;
}

.timeline-item:nth-child(even) .timeline-panel {
    float: right;
}

.timeline-item:nth-child(even) .timeline-panel::after {
    left: -15px;
    border-left: 0;
    border-right: 15px solid #fff;
}

.timeline-heading {
    margin-bottom: 15px;
}

.timeline-heading h3 {
    margin-bottom: 0.5rem;
    color: #212529;
}

.timeline-date, .timeline-institution {
    color: #6c757d;
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
}

/* Learning Cards */
.learning-card {
    background-color: #fff;
    border-radius: 15px;
    padding: 2rem;
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
    height: 100%;
}

.learning-list {
    list-style: none;
    padding-left: 0;
}

.learning-list li {
    position: relative;
    padding-left: 20px;
    margin-bottom: 1.5rem;
}

.learning-list li:last-child {
    margin-bottom: 0;
}

.learning-list li::before {
    content: '';
    position: absolute;
    left: 0;
    top: 10px;
    width: 8px;
    height: 8px;
    background-color: var(--bs-primary);
    border-radius: 50%;
}

.learning-item h4 {
    margin-bottom: 0.5rem;
    color: #212529;
}

.learning-item p {
    color: #6c757d;
    margin-bottom: 0.5rem;
}

/* Responsive Adjustments */
@media (max-width: 991.98px) {
    .timeline::before {
        left: 40px;
    }
    
    .timeline-badge {
        left: 40px;
    }
    
    .timeline-panel {
        width: calc(100% - 90px);
        float: right;
    }
    
    .timeline-panel::after {
        left: -15px;
        border-left: 0;
        border-right: 15px solid #fff;
    }
    
    .certifications-header {
        padding: 4rem 0;
    }
}

@media (max-width: 767.98px) {
    .certification-card {
        padding: 1.5rem;
    }
    
    .certification-logo {
        height: 60px;
    }
    
    .learning-card {
        margin-bottom: 1.5rem;
    }
}

