/**
 * About page specific JavaScript
 */
document.addEventListener('DOMContentLoaded', function() {
    // --- CONSTANTS ---
    const newsletterForm = document.getElementById('newsletterForm');
    const newsletterBtn = document.getElementById('newsletterBtn');
    const newsletterMessage = document.getElementById('newsletterMessage');
    const backToTopBtn = document.getElementById('backToTop');

    // --- INITIALIZATIONS ---
    // Initialize tooltips
    const tooltipTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]'));
    tooltipTriggerList.map(function (tooltipTriggerEl) {
        return new bootstrap.Tooltip(tooltipTriggerEl);
    });

    // --- SKILL ANIMATIONS ---
    // Animate progress bars when they come into view
    const progressBars = document.querySelectorAll('.progress-bar');
    const observer = new IntersectionObserver((entries) => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const progressBar = entry.target;
                const width = progressBar.style.width;
                progressBar.style.width = '0%';
                setTimeout(() => {
                    progressBar.style.transition = 'width 1s ease-in-out';
                    progressBar.style.width = width;
                }, 200);
                observer.unobserve(progressBar);
            }
        });
    }, { threshold: 0.5 });

    progressBars.forEach(bar => {
        observer.observe(bar);
    });

    // --- TIMELINE ANIMATIONS ---
    // Add hover effect to timeline items
    const timelineItems = document.querySelectorAll('.timeline-item');
    timelineItems.forEach(item => {
        item.addEventListener('mouseenter', function() {
            this.querySelector('.timeline-panel').style.transform = 'translateY(-5px)';
            this.querySelector('.timeline-panel').style.boxShadow = '0 10px 20px rgba(0, 0, 0, 0.1)';
        });
        
        item.addEventListener('mouseleave', function() {
            this.querySelector('.timeline-panel').style.transform = 'translateY(0)';
            this.querySelector('.timeline-panel').style.boxShadow = '0 5px 15px rgba(0, 0, 0, 0.05)';
        });
    });

    // --- TESTIMONIAL CAROUSEL ---
    // Initialize the testimonial carousel with custom settings
    const testimonialCarousel = document.getElementById('testimonialCarousel');
    if (testimonialCarousel) {
        const carousel = new bootstrap.Carousel(testimonialCarousel, {
            interval: 5000,
            wrap: true,
            touch: true
        });
        
        // Track testimonial views for analytics
        testimonialCarousel.addEventListener('slide.bs.carousel', function(event) {
            const testimonialName = event.relatedTarget.querySelector('.testimonial-name').textContent;
            if (typeof gtag !== 'undefined' && localStorage.getItem('cookiesAccepted') === 'true') {
                gtag('event', 'testimonial_view', {
                    'event_category': 'engagement',
                    'event_label': testimonialName
                });
            }
        });
    }

    // --- BACK TO TOP BUTTON ---
    if (backToTopBtn) {
        window.addEventListener('scroll', function() {
            if (window.pageYOffset > 300) {
                backToTopBtn.classList.add('show');
            } else {
                backToTopBtn.classList.remove('show');
            }
        });
        backToTopBtn.addEventListener('click', function() {
            window.scrollTo({ top: 0, behavior: 'smooth' });
        });
    }

    // --- NEWSLETTER FORM ---
    if (newsletterForm) {
        newsletterForm.addEventListener('submit', async function(e) {
            e.preventDefault();
            const originalText = newsletterBtn.innerHTML;
            newsletterBtn.innerHTML = '<i class="bi bi-hourglass-split"></i> Subscribing...';
            newsletterBtn.disabled = true;
            
            try {
                const response = await fetch(newsletterForm.action, {
                    method: 'POST',
                    body: new FormData(newsletterForm),
                    headers: { 'Accept': 'application/json' }
                });
                if (response.ok) {
                    newsletterMessage.className = 'alert alert-success mt-2';
                    newsletterMessage.innerHTML = `<i class="bi bi-check-circle me-2"></i><strong>Success!</strong> Thank you for subscribing!`;
                    newsletterForm.reset();
                } else {
                    throw new Error('Network response was not ok');
                }
            } catch (error) {
                newsletterMessage.className = 'alert alert-danger mt-2';
                newsletterMessage.innerHTML = `<i class="bi bi-exclamation-triangle me-2"></i><strong>Oops!</strong> Something went wrong. Please try again.`;
            } finally {
                newsletterMessage.style.display = 'block';
                newsletterBtn.innerHTML = originalText;
                newsletterBtn.disabled = false;
                setTimeout(() => { newsletterMessage.style.display = 'none'; }, 5000);
            }
        });
    }

    // --- PROFILE IMAGE ANIMATION ---
    // Add subtle animation to profile image
    const profileImage = document.querySelector('.profile-image');
    if (profileImage) {
        profileImage.addEventListener('mouseover', function() {
            this.style.transform = 'scale(1.05) rotate(3deg)';
        });
        
        profileImage.addEventListener('mouseout', function() {
            this.style.transform = 'scale(1)';
        });
    }

    // --- TOOL ITEMS INTERACTION ---
    // Add click tracking for tool items
    const toolItems = document.querySelectorAll('.tool-item');
    toolItems.forEach(tool => {
        tool.addEventListener('click', function() {
            const toolName = this.getAttribute('title') || this.getAttribute('data-bs-original-title');
            if (typeof gtag !== 'undefined' && localStorage.getItem('cookiesAccepted') === 'true') {
                gtag('event', 'tool_click', {
                    'event_category': 'engagement',
                    'event_label': toolName
                });
            }
        });
    });
});

// --- GLOBAL FUNCTIONS ---
// Cookie consent functions
function acceptCookies() {
    localStorage.setItem('cookiesAccepted', 'true');
    document.getElementById('cookieConsent').classList.remove('show');
    if (typeof gtag !== 'undefined') {
        gtag('config', 'G-9NKNYWDH33');
    }
}

function declineCookies() {
    localStorage.setItem('cookiesAccepted', 'false');
    document.getElementById('cookieConsent').classList.remove('show');
    window['ga-disable-G-9NKNYWDH33'] = true;
}

js/demo.js.content:
const projects = [
    {
        id: 'portfolio',
        title: 'Personal Portfolio Website',
        description: 'A responsive portfolio website showcasing my skills, projects, and services as a virtual assistant.',
        tech: 'bootstrap,html, css, javascript, usebasin, formspree',
        url: 'https://peterlight123.github.io/portfolio/index.html',
        status: 'live',
        image: 'https://i.imgur.com/YhCPbz5.png'
    },
    {
        id: 'form',
        title: 'Formspree Contact Form',
        description: 'A professional contact form built with HTML, CSS, and Bootstrap, integrated with Formspree',
        tech: 'formspree, HTML, CSS, javascript',
        url: 'https://peterlight123.github.io/formspree/', 
        status: 'demo',
        image: 'https://i.imgur.com/YpEEkNu.png'
    },
    {
        id: 'web3',
        title: 'Dynamic Hero Page',
        description: 'An engaging hero page with interactive animations and a strong call-to-action',
        tech: 'JavaScript, HTML, CSS',
        url: 'https://peterlight123.github.io/website3/',
        status: 'live',
        image: 'https://i.imgur.com/bXmX09r.png'
    },
    {
        id: 'blog',
        title: 'PLS WorldNews',
        description: 'SEO-Optimized Blog',
        tech: 'Blogger, Html',
        url: 'https://plsworldnews.com',
        status: 'live',
        image: 'assets/images/projects/blog.jpg'
    },
    {
        id: 'hospital',
        title: 'Lightspeed hospital template',
        description: 'Modern hospital template',
        tech: 'HTML5, CSS3, JavaScript',
        url: 'https://peterlight123.github.io/lightspeed-hospital-template/', 
        status: 'demo',
        image: 'https://peterlight123.github.io/portfolio/images/projects/lightspeed-hospital.png'
    },
    {
        id: 'basketball',
        title: 'Freedom Reign Basketball academy',
        description: 'Responsive Basketball academy website',
        tech: 'HTML, CSS, Bootstrap',
        url: 'https://peterlight123.github.io/freedomreign/', 
        status: 'live',
        image: 'https://i.imgur.com/sEc0qgE.png'
    }
];

let currentProject = null;

// Initialize demo page
document.addEventListener('DOMContentLoaded', function() {
    loadProjects();
    setupEventListeners();

    // Check if there's a project parameter in URL
    const urlParams = new URLSearchParams(window.location.search);
    const projectId = urlParams.get('project');
    if (projectId) {
        const project = projects.find(p => p.id === projectId);
        if (project) {
            selectProject(project);
        }
    }
});

// Load projects into the selector
function loadProjects() {
    const projectsList = document.getElementById('projectsList');

    projects.forEach(project => {
        const projectCard = createProjectCard(project);
        projectsList.appendChild(projectCard);
    });
}

// Create project card element
function createProjectCard(project) {
    const card = document.createElement('div');
    card.className = 'project-card';
    card.onclick = () => selectProject(project);

    const statusClass = project.status === 'live' ? 'live' : 
                       project.status === 'demo' ? 'demo' : 'coming-soon';

    const statusText = project.status === 'live' ? 'LIVE' : 
                      project.status === 'demo' ? 'DEMO' : 'SOON';

    card.innerHTML = `
        <div class="project-status ${statusClass}">${statusText}</div>
        <div class="project-title">${project.title}</div>
        <div class="project-description">${project.description}</div>
        <div class="project-tech">
            <i class="bi bi-code-slash me-1"></i>${project.tech}
        </div>
    `;

    card.dataset.projectId = project.id;

    return card;
}

// Select and load project
function selectProject(project) {
    if (project.status === 'coming-soon') {
        showComingSoon(project);
        return;
    }

    currentProject = project;

    // Update active state
    document.querySelectorAll('.project-card').forEach(card => {
        card.classList.remove('active');
    });

    const activeCard = document.querySelector(`[data-project-id="${project.id}"]`);
    if (activeCard) {
        activeCard.classList.add('active');
    }

    // Update URL
    document.getElementById('currentUrl').value = project.url;

    // Show loading
    showLoading();

    // Hide no project message
    document.getElementById('noProject').style.display = 'none';

    // Load project in iframe
    const iframe = document.getElementById('demoFrame');
    iframe.src = project.url;

    // Update page title
    document.title = `${project.title} Demo - Peter Eluwade`;

    // Update URL without reload
    const newUrl = new URL(window.location);
    newUrl.searchParams.set('project', project.id);
    window.history.pushState({}, '', newUrl);

    // Track demo view
    if (typeof gtag !== 'undefined') {
        gtag('event', 'demo_view', {
            'event_category': 'engagement',
            'event_label': project.title
        });
    }
}

// Show loading overlay
function showLoading() {
    document.getElementById('loadingOverlay').style.display = 'flex';
}

// Hide loading overlay
function hideLoading() {
    document.getElementById('loadingOverlay').style.display = 'none';
}

// Setup event listeners
function setupEventListeners() {
    const iframe = document.getElementById('demoFrame');

    // Hide loading when iframe loads
    iframe.addEventListener('load', function() {
        hideLoading();
    });

    // Handle iframe errors
    iframe.addEventListener('error', function() {
        hideLoading();
        showError('Failed to load project demo');
    });

    // Keyboard shortcuts
    document.addEventListener('keydown', function(e) {
        if (e.key === 'Escape') {
            exitFullscreen();
        } else if (e.key === 'F5' || (e.ctrlKey && e.key === 'r')) {
            e.preventDefault();
            refreshDemo();
        }
    });
}

// Refresh current demo
function refreshDemo() {
    if (currentProject) {
        showLoading();
        const iframe = document.getElementById('demoFrame');
        iframe.src = iframe.src; // Reload iframe
    }
}

// Open demo in new tab
function openInNewTab() {
    if (currentProject) {
        window.open(currentProject.url, '_blank');

        // Track external view
        if (typeof gtag !== 'undefined') {
            gtag('event', 'external_demo_view', {
                'event_category': 'engagement',
                'event_label': currentProject.title
            });
        }
    }
}

// Toggle fullscreen mode
function toggleFullscreen() {
    const demoViewer = document.querySelector('.demo-viewer');

    if (!document.fullscreenElement) {
        demoViewer.requestFullscreen().catch(err => {
            console.log('Error attempting to enable fullscreen:', err);
        });
    } else {
        document.exitFullscreen();
    }
}

// Exit fullscreen
function exitFullscreen() {
    if (document.fullscreenElement) {
        document.exitFullscreen();
    }
}

// Show coming soon message
function showComingSoon(project) {
    const modal = document.createElement('div');
    modal.className = 'modal fade';
    modal.innerHTML = `
        <div class="modal-dialog modal-dialog-centered">
            <div class="modal-content">
                <div class="modal-header">
                    <h5 class="modal-title">
                        <i class="bi bi-clock-history me-2"></i>Coming Soon
                    </h5>
                    <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
                </div>
                <div class="modal-body text-center">
                    <div class="mb-3">
                        <i class="bi bi-tools" style="font-size: 48px; color: #ffc107;"></i>
                    </div>
                    <h4>${project.title}</h4>
                    <p class="text-muted">${project.description}</p>
                    <p><strong>Tech Stack:</strong> ${project.tech}</p>
                    <div class="alert alert-info">
                        <i class="bi bi-info-circle me-2"></i>
                        This project is currently under development. Check back soon!
                    </div>
                </div>
                <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                    <a href="contact.html" class="btn btn-primary">
                        <i class="bi bi-envelope me-2"></i>Get Notified
                    </a>
                </div>
            </div>
        </div>
    `;

    document.body.appendChild(modal);
    const bsModal = new bootstrap.Modal(modal);
    bsModal.show();

    // Remove modal from DOM when hidden
    modal.addEventListener('hidden.bs.modal', function() {
        document.body.removeChild(modal);
    });
}

// Show error message
function showError(message) {
    const iframe = document.getElementById('demoFrame');
    iframe.src = 'data:text/html,<html><body style="font-family:Arial;padding:50px;text-align:center;color:#666;"><h2> Demo Unavailable</h2><p>' + message + '</p><p>Please try again later or <a href="contact.html">contact me</a> if the issue persists.</p></body></html>';
}

// Utility function to get project by ID
function getProjectById(id) {
    return projects.find(project => project.id === id);
}

// Export functions for global access
window.demoFunctions = {
    selectProject,
    refreshDemo,
    openInNewTab,
    toggleFullscreen,
    getProjectById
};

css/services.css.content:
/* Services Page Specific Styles */

/* Header */
.services-header {
    background: linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.6)), url('https://images.unsplash.com/photo-1557804506-669a67965ba0?w=1200&auto=format&fit=crop') no-repeat center center;
    background-size: cover;
    padding: 6rem 0;
}

.services-header h1 {
    text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
}

/* Service Cards */
.service-card {
    background-color: #fff;
    border: 1px solid #e9ecef;
    border-radius: 1rem;
    padding: 2rem;
    text-align: center;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    display: flex;
    flex-direction: column;
}

.service-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 1rem 3rem rgba(0,0,0,0.1);
}

.service-icon {
    font-size: 3rem;
    margin-bottom: 1rem;
}

.service-features {
    text-align: left;
    margin-top: 1.5rem;
    margin-bottom: 1.5rem;
    padding-left: 0;
}

.service-features li {
    margin-bottom: 0.5rem;
    display: flex;
    align-items: center;
}

.service-features i {
    margin-right: 0.75rem;
}

/* Why Choose Me Section */
.feature-box {
    background-color: #fff;
    border-radius: 1rem;
    transition: transform 0.3s ease;
}

.feature-box:hover {
    transform: scale(1.05);
}

.feature-icon {
    font-size: 2.5rem;
}

/* FAQ Section */
.accordion-item {
    border: none;
    border-bottom: 1px solid #dee2e6;
    border-radius: 0 !important;
}

.accordion-button {
    font-weight: 600;
    color: #212529;
}

.accordion-button:not(.collapsed) {
    background-color: #f8f9fa;
    color: var(--bs-primary);
    box-shadow: none;
}

.accordion-button:focus {
    box-shadow: none;
    border-color: rgba(0,0,0,.125);
}

.accordion-button::after {
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 16 16' fill='%230d6efd'%3e%3cpath fill-rule='evenodd' d='M1.646 4.646a.5.5 0 0 1 .708 0L8 10.293l5.646-5.647a.5.5 0 0 1 .708.708l-6 6a.5.5 0 0 1-.708 0l-6-6a.5.5 0 0 1 0-.708z'/%3e%3c/svg%3e");
}

.accordion-body {
    padding: 1.5rem;
    background-color: #f8f9fa;
}

/* Service Card Specific Colors */
.service-card .btn-outline-primary:hover,
.service-card .btn-outline-danger:hover,
.service-card .btn-outline-info:hover,
.service-card .btn-outline-warning:hover,
.service-card .btn-outline-secondary:hover,
.service-card .btn-outline-success:hover {
    color: white;
}

/* Responsive Adjustments */
@media (max-width: 767.98px) {
    .services-header {
        padding: 4rem 0;
    }
    
    .service-card {
        padding: 1.5rem;
    }
    
    .service-icon {
        font-size: 2.5rem;
    }
}

/* Animation for Service Cards */
@keyframes pulse {
    0% {
        transform: scale(1);
    }
    50% {
        transform: scale(1.05);
    }
    100% {
        transform: scale(1);
    }
}

.service-card:hover .service-icon i {
    animation: pulse 1s infinite;
}

/* Hover effect for feature boxes */
.feature-box:hover .feature-icon {
    transform: rotate(15deg);
    transition: transform 0.3s ease;
}

/* Smooth scroll behavior */
html {
    scroll-behavior: smooth;
}

/* Service anchor offset for smooth scrolling */
#web-development, #graphic-design, #social-media, 
#content-creation, #data-entry, #digital-marketing {
    scroll-margin-top: 80px;
}
/* Service highlighting */
@keyframes highlight-pulse {
    0% {
        box-shadow: 0 0 0 0 rgba(13, 110, 253, 0.7);
    }
    70% {
        box-shadow: 0 0 0 15px rgba(13, 110, 253, 0);
    }
    100% {
        box-shadow: 0 0 0 0 rgba(13, 110, 253, 0);
    }
}

.highlight-service {
    animation: highlight-pulse 2s infinite;
    border: 2px solid var(--bs-primary);
}

/* End of Services Page Specific Styles */

css/hero.css.content:
/* Hero Section Styling */
.hero-section {
    background: 
        linear-gradient(135deg, rgba(13, 110, 253, 0.85) 0%, rgba(10, 88, 202, 0.9) 100%),
        url('../images/backgrounds/hero-background.jpg') center/cover no-repeat;
    min-height: 100vh;
    padding: 100px 0 120px;
    position: relative;
    overflow: hidden;
}

.hero-content {
    position: relative;
    z-index: 2;
    padding-right: 20px;
}

.hero-image-container {
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 2;
}

.hero-image {
    position: relative;
    width: 320px;
    height: 320px;
    border-radius: 50%;
    overflow: hidden;
    border: 5px solid rgba(255, 255, 255, 0.2);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
    animation: float 6s ease-in-out infinite;
}

.hero-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.hero-buttons .btn {
    padding: 12px 24px;
    font-weight: 600;
    transition: all 0.3s ease;
}

.hero-buttons .btn:hover {
    transform: translateY(-3px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
}

.hero-buttons .btn-danger {
    background-color: #dc3545;
    border-color: #dc3545;
}

.hero-buttons .btn-danger:hover {
    background-color: #c82333;
    border-color: #bd2130;
}

.social-icons a {
    transition: all 0.3s ease;
    opacity: 0.8;
}

.social-icons a:hover {
    opacity: 1;
    transform: translateY(-3px);
}

.hero-shape-bottom {
    position: absolute;
    bottom: -1px;
    left: 0;
    width: 100%;
    z-index: 1;
    line-height: 0;
}

/* Floating animation for profile image */
@keyframes float {
    0% {
        transform: translateY(0px);
    }
    50% {
        transform: translateY(-15px);
    }
    100% {
        transform: translateY(0px);
    }
}

/* Responsive adjustments */
@media (max-width: 991.98px) {
    .hero-section {
        padding: 80px 0 100px;
        min-height: auto;
    }

    .hero-image {
        width: 280px;
        height: 280px;
        margin-top: 30px;
    }
}

@media (max-width: 767.98px) {
    .hero-section {
        text-align: center;
        padding: 60px 0 80px;
    }

    .hero-content {
        padding-right: 0;
        margin-bottom: 40px;
    }

    .hero-image {
        width: 240px;
        height: 240px;
    }

    .social-icons {
        justify-content: center;
    }
}

@media (max-width: 575.98px) {
    .hero-section {
        padding: 40px 0 60px;
    }

    h1.display-4 {
        font-size: 2.5rem;
    }

    .hero-image {
        width: 200px;
        height: 200px;
    }
}
